<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuIncapacidadPagoRepository extends EntityRepository {
   
    public function listaDQL($strCodigoEntidadSalud = "") {                
        $dql   = "SELECT ip FROM BrasaRecursoHumanoBundle:RhuIncapacidadPago ip WHERE ip.codigoIncapacidadPagoPk <> 0";
        if($strCodigoEntidadSalud != "") {
            $dql .= " AND ip.codigoEntidadExamenFk = " . $strCodigoEntidadSalud;
        }     
        
        $dql .= " ORDER BY ip.codigoIncapacidadPagoPk";
        return $dql;
    } 
    
    public function eliminarIncapacidadPagoSeleccionados($arrSeleccionados) {        
        if(count($arrSeleccionados) > 0) {
            $em = $this->getEntityManager();
            foreach ($arrSeleccionados AS $codigoIncapacidadPago) {                
                $arIncapacidadPago = $em->getRepository('BrasaRecursoHumanoBundle:RhuIncapacidadPago')->find($codigoIncapacidadPago);                     
                if($em->getRepository('BrasaRecursoHumanoBundle:RhuIncapacidadPago')->devuelveRegistrosIncapacidadPagosDetalle($codigoIncapacidadPago) <= 0){   
                    $em->remove($arIncapacidadPago);
                }
            }
            $em->flush();       
        }     
    }
    
    public function devuelveRegistrosIncapacidadPagosDetalle($codigoIncapacidadPago) {
        $em = $this->getEntityManager();
        $dql   = "SELECT COUNT(ipd.codigoIncapacidadPagoDetallePk) FROM BrasaRecursoHumanoBundle:RhuIncapacidadPagoDetalle ipd WHERE ipd.codigoIncapacidadPagoFk = " . $codigoIncapacidadPago;
        $query = $em->createQuery($dql);
        $douRegistrosIncapacidadPagoDetalle = $query->getSingleScalarResult();
        return $douRegistrosIncapacidadPagoDetalle;
    }
    
    public function liquidar($codigoIncapacidadPago) {
        $em = $this->getEntityManager();
        $arIncapacidadPago = $em->getRepository('BrasaRecursoHumanoBundle:RhuIncapacidadPago')->find($codigoIncapacidadPago);
        $arIncapacidadPagoDetalles = $em->getRepository('BrasaRecursoHumanoBundle:RhuIncapacidadPagoDetalle')->findBy(array('codigoIncapacidadPagoFk' => $codigoIncapacidadPago));
        $douTotal = 0;
        foreach ($arIncapacidadPagoDetalles AS $arIncapacidadPagoDetalle) {
            $douTotal += $arIncapacidadPagoDetalle->getVrPago();
        }
        $arIncapacidadPago->setVrTotal($douTotal);
        $em->persist($arIncapacidadPago);
        $em->flush();
    }
}